    @log_request("/thread/new->list")
    def requestGetNewPost(self, from_id, from_time):
        self.updateHeader()
        url = "https://api.x.zanao.com/thread/new"
        data = {
            "type": "list",
            "from_id": from_id,
            "from_time": from_time,
            "with_comment": "true",
            "with_reply": "true"
        }
        response = requests.post(url, headers=self.headers, data=data, verify=False)
        logger.info(f'url:{url}request:{data}response:{response.text}')
        return response

